import React from 'react';
import Grid from '../cmp/grid';


export default class WordSearch extends React.Component {
    constructor(props) {
      super(props);
      let wordList = ['house', 'cat', 'leg', 'react', 'hello'];
      let rowCount = 10;
      let colCount = 10;
      this.grid = getWordGrid(wordList, rowCount, colCount);
    }
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <Grid values={this.grid} />
          </div>
          <div className="game-info">
            <div>{this.wordList}</div>
            <ol>Welcome to Word Sreach react game</ol>
          </div>
        </div>
      );
    }
  }
